function surface(input) {
    const u = input.u; // Longitude (-PI to PI)
    const v = input.v; // Latitude (-PI/2 to PI/2)

    // Superformula parameters
    const m = input.m;
    const n1 = input.n1;
    const n2 = input.n2;
    const n3 = input.n3;
    const a = input.a;
    const b = input.b;

    // Helper for superformula radius
    function superShape(angle, m, n1, n2, n3, a, b) {
        const t1 = Math.abs((1/a) * Math.cos(m * angle / 4));
        const t2 = Math.abs((1/b) * Math.sin(m * angle / 4));
        return Math.pow(Math.pow(t1, n2) + Math.pow(t2, n3), -1 / n1);
    }

    const r1 = superShape(u, m, n1, n2, n3, a, b);
    const r2 = superShape(v, m, n1, n2, n3, a, b);

    const x = r1 * Math.cos(u) * r2 * Math.cos(v) * 5;
    const y = r1 * Math.sin(u) * r2 * Math.cos(v) * 5;
    const z = r2 * Math.sin(v) * 5;

    // Coloring based on radius extension
    const dist = Math.sqrt(x*x + y*y + z*z);
    const r = 0.5 + 0.5 * Math.sin(dist * 0.5 + input.m);
    const g = 0.5 + 0.5 * Math.cos(u * 2);
    const b_col = 0.5 + 0.5 * Math.sin(v * 3);

    return { x, y, z, r, g, b: b_col, a: 1 };
}
