function surface(input) {
    const u = input.u;
    const v = input.v;
    const a = 1; // Hardcoded scale a
    const b = input.b !== undefined ? input.b : 0.2;
    const t = input.t || 0;

    // Animate the twist
    const dynamicB = b + 0.1 * Math.sin(t);

    const x = a * Math.cos(u) * Math.sin(v);
    const y = a * Math.sin(u) * Math.sin(v);
    const z = a * (Math.cos(v) + Math.log(Math.tan(v / 2))) + dynamicB * u;

    // Color based on height (z) and twist angle (u)
    // Create a metallic/neon gradient
    const colorVal = (z * 0.3 + u * 0.1 + t * 0.2) % 1;

    // Palette: Deep Purple to Cyan
    const r_col = 0.2 + 0.6 * Math.sin(colorVal * Math.PI * 2);
    const g_col = 0.2 + 0.6 * Math.sin(colorVal * Math.PI * 2 + 2);
    const b_col = 0.8;

    return { x, y, z, r: r_col, g: g_col, b: b_col, a: 1 };
}
