function surface(input) {
    const u = input.u;
    const v = input.v;

    // X Terms
    const x = input.x_sin_u * Math.sin(u) 
            + input.x_cos_u * Math.cos(u)
            + input.x_sin_v * Math.sin(v)
            + input.x_cos_v * Math.cos(v)
            + input.x_sin_2u * Math.sin(2*u)
            + input.x_cos_2u * Math.cos(2*u)
            + input.x_sin_2v * Math.sin(2*v)
            + input.x_cos_2v * Math.cos(2*v)
            + input.x_sin_3u * Math.sin(3*u)
            + input.x_mix_uv * Math.sin(u) * Math.cos(v);

    // Y Terms
    const y = input.y_sin_u * Math.sin(u) 
            + input.y_cos_u * Math.cos(u)
            + input.y_sin_v * Math.sin(v)
            + input.y_cos_v * Math.cos(v)
            + input.y_sin_2u * Math.sin(2*u)
            + input.y_cos_2u * Math.cos(2*u)
            + input.y_sin_2v * Math.sin(2*v)
            + input.y_cos_2v * Math.cos(2*v)
            + input.y_sin_3v * Math.sin(3*v)
            + input.y_mix_uv * Math.cos(u) * Math.sin(v);

    // Z Terms
    const z = input.z_sin_u * Math.sin(u) 
            + input.z_cos_u * Math.cos(u)
            + input.z_sin_v * Math.sin(v)
            + input.z_cos_v * Math.cos(v)
            + input.z_sin_2u * Math.sin(2*u)
            + input.z_cos_2u * Math.cos(2*u)
            + input.z_sin_2v * Math.sin(2*v)
            + input.z_cos_2v * Math.cos(2*v)
            + input.z_sin_3u * Math.sin(3*u)
            + input.z_mix_uv * Math.sin(u + v);

    // Color Mapping (RGB Fourier)
    const p = input.c_phase;

    const r = 0.5 + 0.5 * Math.sin(input.cr_freq_u * u + input.cr_freq_v * v + p);
    const g = 0.5 + 0.5 * Math.sin(input.cg_freq_u * u + input.cg_freq_v * v + p + 2.09);
    const b = 0.5 + 0.5 * Math.sin(input.cb_freq_u * u + input.cb_freq_v * v + p + 4.18);

    return { x, y, z, r, g, b };
}
