function surface(input) {
    const u = input.u;
    const v = input.v;
    const R = input.radius;
    const L = input.length;
    const halfL = L / 2;
    const sausageLen = L * 1.15;
    const halfSausage = sausageLen / 2;

    let x, y, z, r, g, b;

    if (v < 0.45) {
        // BUN - wraps around bottom, opening faces up (+Z)
        const t = v / 0.45;
        const bunR = R * 1.5;
        const angle = Math.PI * 0.25 + t * Math.PI * 1.5;

        const uNorm = u / (2 * Math.PI);
        const xBase = -halfL + uNorm * L;

        const endMargin = bunR * 0.6;
        let endScale = 1;
        if (xBase < -halfL + endMargin) {
            const d = (-halfL + endMargin - xBase) / endMargin;
            endScale = Math.sqrt(Math.max(0, 1 - d*d));
        } else if (xBase > halfL - endMargin) {
            const d = (xBase - (halfL - endMargin)) / endMargin;
            endScale = Math.sqrt(Math.max(0, 1 - d*d));
        }

        x = xBase;
        y = bunR * Math.sin(angle) * endScale;
        z = bunR * Math.cos(angle) * endScale + R * 0.1;

        r = 0.94; g = 0.74; b = 0.44;

    } else if (v < 0.88) {
        // SAUSAGE - longer capsule, sticks out of bun
        const t = (v - 0.45) / 0.43;
        const capSize = 0.1;
        let xPos, rScale;

        if (t < capSize) {
            const ct = t / capSize;
            const phi = (ct - 1) * Math.PI / 2;
            rScale = Math.cos(phi);
            xPos = -halfSausage + R + R * Math.sin(phi);
        } else if (t > 1 - capSize) {
            const ct = (t - (1 - capSize)) / capSize;
            const phi = ct * Math.PI / 2;
            rScale = Math.cos(phi);
            xPos = halfSausage - R + R * Math.sin(phi);
        } else {
            const mt = (t - capSize) / (1 - 2*capSize);
            rScale = 1;
            xPos = -halfSausage + R + mt * (sausageLen - 2*R);
        }

        x = xPos;
        y = R * rScale * Math.sin(u);
        z = R * rScale * Math.cos(u);

        const grill = Math.sin(u * 5) > 0.55 ? 0.2 : 0;
        r = 0.82 - grill;
        g = 0.32 - grill;
        b = 0.16 - grill * 0.4;

    } else {
        // KETCHUP - on top of sausage (+Z direction)
        const t = (v - 0.88) / 0.12;
        const kR = R * 0.08;
        const uNorm = u / (2 * Math.PI);

        const xPos = -halfL * 0.5 + uNorm * L * 0.5;
        const wave = Math.sin(uNorm * 20) * R * 0.2;
        const angle = t * 2 * Math.PI;

        x = xPos;
        y = wave + kR * Math.sin(angle);
        z = R + kR * (1 + Math.cos(angle));

        r = 0.92; g = 0.1; b = 0.08;
    }

    return { x, y, z, r, g, b, a: 1 };
}
