function surface(input) {
    const u = input.u;
    const v = input.v;
    const radius = input.radius || 4;
    const width = input.width || 1.5;
    const twists = Math.round(input.twists !== undefined ? input.twists : 1);
    const turn = input.turn !== undefined ? input.turn : 0;

    const twistAngle = twists * u / 2 + Math.PI * turn;
    const cosTwist = Math.cos(twistAngle);
    const sinTwist = Math.sin(twistAngle);
    const uPos = twists !== 0 ? (u - (2 * Math.PI * turn) / twists) : u;
    const cosU = Math.cos(uPos);
    const sinU = Math.sin(uPos);

    const r_local = radius + (v * width / 2) * cosTwist;

    const x = r_local * cosU;
    const y = r_local * sinU;
    const z = (v * width / 2) * sinTwist;

    const r = 0.6 + 0.3 * cosU;
    const g = 0.7 + 0.2 * sinU;
    const b = 0.85;

    return { x, y, z, r, g, b, a: 1 };
}
